package com.java;

// Tarea 1. Realizar un programa que sume 3 variables.

public class Tarea1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int var1 = 10;
		int var2 = 5;
		int var3 = 15;
		int total = 0;
		
		System.out.println("El valor de var1 es: " + var1);
		System.out.println("El valor de var2 es: " + var2);
		System.out.println("El valor de var3 es: " + var3);
		
		total = var1 + var2 + var3;
				
		System.out.println("La suma es: " + total); // Se realiza la suma
	}
}
